import React, { Component, useState } from "react";
import './SavedMoviesList.css'


const SavedMoviesList = (props) => {
    console.log('passed db data as json', props.savedmovies)
    if (props.savedmovies != null && props.savedmovies.Response != 'False') {
        return (
            <div id='savedMovieComponentId'>
                <div className="no-column">
                    <div className="MovieLists">
                        {
                            props.savedmovies.data.map((item, i) =>
                                <li>
                                    <div>
                                        <img className="image" src={item.poster} />
                                    </div>
                                    <div>
                                        <b>{item.released}</b>
                                        <h2>{item.title}</h2>
                                        <p>{item.genre}</p>
                                        <p>{item.ratings}</p>
                                        {item.Source}: <span>{item.Value}</span>
                                    </div>


                                </li>
                            )
                        }</div>

                </div>
            </div>


        )

    } else {
        return (
            <p className="Error">Something went Wrong!!</p>
        )
    }

}
export default SavedMoviesList;
