    function getshowMovie(val) {
        setshowMovie(val.target.value)
    }

    const handleClick = (movieName) => {
        movieName = document.getElementById('movieId').value;
        console.log('Searching for ' + movieName);
        showMovie = true;
        console.log('showMovie: ' + showMovie);

    }

