import MovieDetails from './MovieDetails';
import './SearchMovieForm.css';
import React, { useState } from 'react';
function SearchMovieForm() {
    const [data, setData] = useState(null);
    const handleClick = (movieName) => {
        movieName = data;
        console.log('Searching for ' + movieName);
    }
    return (
        <div className='SearchMovieForm'>
            <input id="movieId" type="text" name="movieName" placeholder='Enter movie name to search here' />
            <button name='search' onClick={() => handleClick()} >Search</button>
        </div>
    );
}

export default SearchMovieForm;
